mapper.select:
SELECT USERNO, USERID, USERNM, USERPW, PHOTO, CU.DEPTNO, DEPTNM, CC.CODENM USERPOS FROM COM_USER CU INNER JOIN COM_DEPT CD ON CD.DEPTNO=CU.DEPTNO INNER JOIN COM_CODE CC ON CC.CODECD=CU.USERPOS AND CLASSNO='3' WHERE CU.DELETEFLAG='N' AND CD.DELETEFLAG='N'
AND CU.DEPTNO=#{searchExt1}
AND CU.USERNM LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.update:
UPDATE COM_USER
   SET USERNM=#{usernm}
     , USERROLE=#{userrole}

         , PHOTO=#{photo}

 WHERE DELETEFLAG='N'
   AND USERNO=#{userno}

UPDATE COM_USER
   SET USERNM=#{usernm}

         , PHOTO=#{photo}

 WHERE DELETEFLAG='N'
   AND USERNO=#{userno}

mapper.delete:
UPDATE COM_USER
   SET DELETEFLAG='Y'
 WHERE USERNO=#{userno}

mapper.update:
UPDATE COM_USER
   SET USERPW=SHA2(#{userpw}, 256)
 WHERE USERNO=#{userno}
