mapper.sql:
WHERE BRDDELETEFLAG='N' AND TBG.BGDELETEFLAG='N'

    AND TB.BGNO=#{bgno}



         ${item} LIKE '%'||#{searchKeyword}||'%'

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY TB.BRDNO DESC) AS RNUM , TBG.BGNAME , TB.BRDNO , TB.BRDTITLE , TB.BRDMEMO , TO_CHAR(TB.BRDDATE, 'YYYY-MM-DD') AS BRDDATE , TB.BRDLIKE , TB.USERNO , USERNM BRDWRITER , TB.BGNO , TB.ETC1 , TB.ETC2 , TB.ETC3 , TB.ETC4 , TB.ETC5 , (SELECT COUNT(*) FROM TBL_BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM TBL_BOARDFILE WHERE BRDNO=TB.BRDNO) FILECNT , (SELECT COUNT(*) FROM TBL_BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM TBL_BOARD TB INNER JOIN COM_USER CU ON TB.USERNO=CU.USERNO INNER JOIN TBL_BOARDGROUP TBG ON TBG.BGNO=TB.BGNO
)
OFFSET ${rowStart-1} ROWS FETCH FIRST ${rowStart-1}+${displayRowCount} ROWS ONLY

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY TB.BRDNO DESC) AS RNUM , BRDNO, BRDTITLE, TO_CHAR(BRDDATE,'YYYY-MM-DD') BRDDATE, BRDNOTICE, BRDLIKE , TB.USERNO, USERNM BRDWRITER, BGNAME, TB.BGNO , (SELECT COUNT(*) FROM TBL_BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM TBL_BOARDFILE WHERE BRDNO=TB.BRDNO) FILECNT , (SELECT COUNT(*) FROM TBL_BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM TBL_BOARD TB LEFT OUTER JOIN COM_USER CU ON TB.USERNO=CU.USERNO INNER JOIN TBL_BOARDGROUP TBG ON TBG.BGNO=TB.BGNO WHERE BRDDELETEFLAG='N' AND BRDNOTICE='Y'
AND TB.BGNO=#{bgno}
) WHERE RNUM BETWEEN 0 AND 3

mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDMEMO=#{brdmemo}
     , BRDNOTICE=#{brdnotice, jdbcType=VARCHAR}
     , LASTDATE=SYSDATE
     , LASTUSERNO=#{userno}
     , ETC1=#{etc1, jdbcType=VARCHAR}
     , ETC2=#{etc2, jdbcType=VARCHAR}
     , ETC3=#{etc3, jdbcType=VARCHAR}
     , ETC4=#{etc4, jdbcType=VARCHAR}
     , ETC5=#{etc5, jdbcType=VARCHAR}
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

mapper.insert:
UPDATE TBL_BOARD
   SET BRDLIKE = BRDLIKE + 1 
 WHERE BRDNO=#{field1}

mapper.delete:
UPDATE TBL_BOARD
   SET BRDDELETEFLAG='Y'
     , LASTDATE=SYSDATE
     , LASTUSERNO=#{userno}
 WHERE BRDNO=#{brdno}


DELETE 
  FROM TBL_BOARDFILE
 WHERE FILENO IN (

             ${item}

)

mapper.insert:

    SELECT NVL(MAX(RENO),0)+1 FROM TBL_BOARDREPLY

INSERT INTO TBL_BOARDREPLY(BRDNO, RENO, USERNO, REDELETEFLAG, REMEMO, REDATE, LASTDATE, LASTUSERNO, REORDER, REPARENT, REDEPTH)
VALUES (#{brdno}, #{reno}, #{userno}, 'N', #{rememo}, SYSDATE, SYSDATE, #{userno}, #{reorder},

               #{reno}, 0
               #{reparent}, #{redepth}

       )

mapper.update:

UPDATE TBL_BOARDREPLY
   SET REORDER = REORDER + 1
 WHERE BRDNO=#{brdno} AND REORDER > #{reorder}

mapper.select:
SELECT NVL(MAX(REORDER),0)+1
  FROM TBL_BOARDREPLY
 WHERE BRDNO=#{brdno}

mapper.update:

UPDATE TBL_BOARDREPLY
   SET REDELETEFLAG='Y' 
     , LASTDATE=SYSDATE
     , LASTUSERNO=#{userno}
 WHERE RENO=#{reno}

UPDATE TBL_BOARDREPLY
   SET REMEMO=#{rememo} 
     , LASTDATE=SYSDATE
     , LASTUSERNO=#{userno}
 WHERE RENO=#{reno}
