mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY TC.CRNO DESC) AS RNUM , CRNO, CRTITLE, TC.USERNO, USERNM, CRMEMO, TO_CHAR(CRDATE,'YYYY-MM-DD') CRDATE FROM TBL_CRUD TC LEFT OUTER JOIN COM_USER CU ON TC.USERNO=CU.USERNO
)
WHERE RNUM BETWEEN ${rowStart} AND ${rowStart-1}+${displayRowCount} -- OFFSET ${rowStart-1} ROWS FETCH FIRST ${displayRowCount} ROWS ONLY -- oracle12c

mapper.update:
UPDATE TBL_CRUD
   SET CRTITLE=#{crtitle}, CRMEMO=#{crmemo}
 WHERE CRNO=#{crno}

mapper.delete:
UPDATE TBL_CRUD
   SET CRDELETEFLAG='Y'
 WHERE CRNO=#{crno}
