mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY EMNO DESC) AS RNUM , EMNO, EMTYPE, EMSUBJECT, EMFROM, TO_DATE(ENTRYDATE,'YYYY-MM-DD HH24:MI') ENTRYDATE, EM.USERNO, USERNM , ( SELECT EAADDRESS FROM ( SELECT ROW_NUMBER() OVER (ORDER BY EASEQ) AS RNUM2, EAADDRESS FROM EML_ADDRESS WHERE EMNO=EM.EMNO AND EATYPE='T' ) WHERE RNUM2 = 1 ) STRTO FROM EML_MAIL EM INNER JOIN COM_USER CU ON EM.USERNO=CU.USERNO
)
WHERE RNUM BETWEEN ${rowStart-1} AND ${rowStart-1}+10

mapper.update:
UPDATE EML_MAIL
   SET EMSUBJECT=#{emsubject}, EMCONTENTS=#{emcontents}
 WHERE EMNO=#{emno}

mapper.delete:
UPDATE EML_MAIL
   SET DELETEFLAG='Y'
 WHERE EMNO=#{emno}
