mapper.update:
UPDATE PRJ_PROJECT
   SET PRTITLE=#{prtitle}
     , PRSTATUS=#{prstatus}
     , PRSTARTDATE=#{prstartdate} 
     , PRENDDATE=#{prenddate}
 WHERE DELETEFLAG='N'
   AND PRNO=#{prno}

mapper.delete:
UPDATE PRJ_PROJECT
   SET DELETEFLAG='Y'
 WHERE PRNO=#{prno}

mapper.update:
UPDATE PRJ_TASK PT1
SET TSPARENT=TSNO
WHERE EXISTS (
     SELECT 0
     FROM PRJ_TASK PT2
     WHERE PT2.PRNO=PT1.PRNO AND PT2.OLDNO=PT1.TSPARENT
       AND PT1.DELETEFLAG='N' AND PT1.PRNO=#{prno}
       AND PT2.DELETEFLAG='N' AND PT2.PRNO=#{prno}
 )
